mapper.update:
update topic
set post_num=post_num+1
where id=#{topicId}

update topic_post
set reply_num=reply_num+1
where id=#{postId}

update qa_question
set answer_num=answer_num+1
where id=#{qAId}

update qa_answer
set reply_num=reply_num+1
where id=#{answerId}

update article
set post_num=post_num+1
where id=#{articleId}

update article
set is_checked = 1
where id=#{articleId}

update user_info
set salt=''
where user_name=#{userName}
